import React, { useState } from "react";
import "./../assets/style/filters.css";

const Filters = () => {
  const [sortingOption, setSortingOption] = useState("");

  const onSortingOptionChange = e => {
    setSortingOption()
  }

  return (
    <form>
      <h4>Sort</h4>
      <div className="sorting-btns">
        <input
          type="radio"
          id="ascending"
          name="ascending"
          value="ascending"
          onChange={onSortingOptionChange}
        />
        <label htmlFor="ascending">
          <button>Ascending(A-Z)</button>
        </label>
        <input
          type="radio"
          id="descending"
          name="descending"
          value="descending"
          onChange={onSortingOptionChange}
        />
        <label htmlFor="descending">
          <button>Descending(Z-A)</button>
        </label>
      </div>
    </form>
  );
};

export default Filters;
