import React, { useEffect, useState } from "react";
import Pagination from "./components/Pagination";
import axios from "axios";

const App = () => {
  const [countries, setCountries] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [countriesPerPage, setCountriesPerPage] = useState(20);

  useEffect(async () => {
    const response = await axios.get(
      "https://restcountries.com/v2/all?fields=name,region,area"
    );
    setCountries(response.data);
    // axios
    //   .get("https://restcountries.com/v2/all?fields=name,region,area")
    //   .then((response) => {
    //     setCountries(response.data);
    //   });
  }, []);

  const lastPostIndex = currentPage * countriesPerPage;
  const firstPostIndex = lastPostIndex - countriesPerPage;
  const currentCountries = countries.slice(firstPostIndex, lastPostIndex);

  return (
    <div className="app">
      <form>
        <h6>Sort</h6>
        <div className="buttons">
          <input type="radio" id="ascending" name="ascending" value="ascending" />
          <label htmlFor="ascending"><button>Ascending(A-Z)</button></label>
          <input type="radio" id="descending" name="descending" value="descending" />
          <label htmlFor="descending"><button>descending(A-Z)</button></label>
        </div>
      </form>
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Country Name</th>
              <th>Region</th>
              <th>Area</th>
            </tr>
          </thead>
          <tbody>
            {currentCountries.map((country, index) => {
              return (
                <tr key={index}>
                  <td>{(currentPage - 1) * countriesPerPage + index + 1}</td>
                  <td>{country.name}</td>
                  <td>{country.region}</td>
                  <td>{country.area}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      <Pagination
        totalCountries={countries.length}
        countriesPerPage={countriesPerPage}
        setCurrentPage={setCurrentPage}
        currentPage={currentPage}
      />
    </div>
  );
};

export default App;
