import React, { useState } from "react";
import "./../assets/style/filters.css";

const Filters = () => {
  const [sortingOption, setSortingOption] = useState("");

  const onSortingOptionChange = (e) => {
    e.preventDefault();
    setSortingOption(e.target.value);
  };

  console.log(sortingOption)

  return (
    <div className="filters">
      <h4>Sort</h4>
      <div className="sorting-btns">
        <button onClick={onSortingOptionChange} value="Ascending(A-Z)">Ascending(A-Z)</button>
        <button onClick={onSortingOptionChange} value="Descending(Z-A)">Descending(Z-A)</button>
      </div>
    </div>
  );
};

export default Filters;
