import React, { useEffect, useState } from "react";
import "./../assets/style/filters.css";

const Filters = ({ countries, setCountries }) => {
  const [sortingOption, setSortingOption] = useState("");

  const onSortingOptionChange = (e) => {
    e.preventDefault();
    setSortingOption(e.target.value);
  };

  useEffect(() => {
    if (sortingOption === "Ascending(A-Z)") {
      const countriesAsc = [...countries].sort((counPrev, counNext) => {
        counPrev.name > counNext.name ? 1 : -1;
      });
      setCountries(countriesAsc);
    } else if (sortingOption === "Descending(Z-A)") {
      const countriesDesc = [...countries].sort((counPrev, counNext) => {
        counPrev.name > counNext.name ? -1 : 1;
      });
      setCountries(countriesDesc);
    }
  }, [sortingOption]);

  console.log(sortingOption);

  return (
    <div className="filters">
      <h4>Sort</h4>
      <div className="sorting-btns">
        <button
          className={sortingOption === "Ascending(A-Z)" && "active-sorting-btn"}
          onClick={onSortingOptionChange}
          value="Ascending(A-Z)"
        >
          Ascending (A-Z)
        </button>
        <button
          className={
            sortingOption === "Descending(Z-A)" && "active-sorting-btn"
          }
          onClick={onSortingOptionChange}
          value="Descending(Z-A)"
        >
          Descending (Z-A)
        </button>
      </div>
    </div>
  );
};

export default Filters;
