import React, { useEffect, useState } from "react";
import axios from "axios";

const App = () => {
  const [countries, setCountries] = useState([]);

  useEffect(() => {
    axios
      .get("https://restcountries.com/v2/all?fields=name,region,area")
      .then((response) => {
        setCountries(response.data);
      });
  }, []);

  console.log(countries);

  return (
    <div className="row align-items-center justify-content-center py-5">
      <div className="col-12">
        <table className="table w-75">
          <thead className="table-dark">
            <tr>
              <th className="py-3 text-center fs-xs-1">#</th>
              <th className="py-3 text-center fs-xs-1">Country Name</th>
              <th className="py-3 text-center fs-xs-1">Region</th>
              <th className="py-3 text-center fs-xs-1">Area Size</th>
            </tr>
          </thead>
          <tbody>
            {countries.map((country, index) => {
              return (
                <tr>
                  <td className="text-center py-3">{index + 1}</td>
                  <td className="text-center py-3">{country.name}</td>
                  <td className="text-center py-3">{country.region}</td>
                  <td className="text-center py-3">{country.area}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default App;
