import React, { useState } from "react";
import "./../assets/style/filters.css";

const Filters = () => {
  const [sortingOption, setSortingOption] = useState("");

  const onSortingOptionChange = (e) => {
    e.preventDefault();
    setSortingOption(e.target.value);
  };

  console.log(sortingOption);

  return (
    <form>
      <h4>Sort</h4>
      <div className="sorting-btns">
        <input
          type="radio"
          id="ascending"
          name="ascending"
          value="ascending"
        />
        <label htmlFor="ascending">
          <button onClick={onSortingOptionChange}>Ascending(A-Z)</button>
        </label>
        <input
          type="radio"
          id="descending"
          name="descending"
          value="descending"
        />
        <label htmlFor="descending">
          <button onClick={onSortingOptionChange}>Descending(Z-A)</button>
        </label>
      </div>
    </form>
  );
};

export default Filters;
